import React, { useState } from 'react'

const AddContact = () => {

  const[name,setName] = useState("");
  const [fullName,setfullName] = useState();

  // const[email,setEmail] = useState("");
  // const [emailAdd,setEmailAdd] = useState();

  const [allInfo,setInfo] = useState([]);

  const inputUser =(e)=>{
    console.log(e.target.value);
    setName(e.target.value);
  }
  // const inputEmail =(e)=>{
  //   console.log(e.target.value);
  //   setEmail(e.target.value);
  // }

  const changeName = (e)=>{
    e.preventDefault();
    // setfullName(name);
    // setEmailAdd(email);
    setInfo((oldItems)=>{
      return [...oldItems,name];
    });
  }

   return (
    <>
    <div className = "ui main">
      <h3>User Details :</h3>
      <form onSubmit = {changeName}className="ui form">
    <div className="field">
    <label>User Name</label>
    <input type="text" name="first-name" placeholder="Enter User name" onChange = {inputUser} />
    </div>
    <div className="field">
    {/* <label>Email Address</label> */}
    {/* <input type="email" name="email" placeholder="Enter email Address" onChange = {inputEmail} /> */}
    </div>
    <div className="field">
    </div>
  <button className="ui button blue" type="Submit">Add User</button>
  
  {/* <h2>{fullName} {emailAdd}</h2> */}

    {allInfo.map((itemval) => {
      return <h2>{itemval} </h2>
    })}

  </form>
    </div>
    </>
  )
}

export default AddContact
