import React, { useState } from 'react'

const UserManagement = () => {

    const[userRegistration, setUser] = useState({
        user:"",
        email:"",
        phone:""
    });

    const handleInput = (e) =>{
        const name = e.target.name;
        // console.log(name);
        // const value = e.target.value;
        // console.log(name,value);

        // setUser(...userRegistration,{[name]:value});
    }

  return (
    <>
    <div className = "ui main">
    <h3>User Details :</h3>
    <form className="ui form">
    <div className="field">
    <label htmlfor = "user">Full Name</label>
    <input type="text"  value = {userRegistration.username} onChange = {handleInput} name="username" id = "username" placeholder="Please enter full name" />
    </div>

    <div className="field">
    <label htmlfor = "email">Email</label>
    <input type="text"  value = {userRegistration.email} onChange = {handleInput} name="email" id = "email" placeholder="Please enter Email" />
    </div>

    <div className="field">
    <label htmlfor = "phone">Contact</label>
    <input type="text"  value = {userRegistration.phone} onChange = {handleInput} name="phone" id = "phone" placeholder="Please enter Contact number" />
    </div>



    <div className="field">
    <button className="ui button blue" type="Submit">Add User</button>
    </div>
    </form>
    </div>

    </>
  )
}

export default UserManagement
