import React, { useState } from 'react'

const AddContact = () => {

  const[name,setName] = useState("");
  const [fullName,setfullName] = useState();

  const[email,setPass] = useState("");
  const [password,setPassword] = useState();

  const inputUser =(e)=>{
    console.log(e.target.value);
    setName(e.target.value);
  }

  const inputPass =(e)=>{
    console.log(e.target.value);
    setName(e.target.value);
  }

  const changeName = (e)=>{
    e.preventDefault();
    setfullName(name);
  }

  return (
    <>
    <div className = "ui main">
      <h3>User Details :</h3>
      <form onSubmit = {changeName}className="ui form">
    <div className="field">
    <label>First Name</label>
    <input type="text" name="first-name" placeholder="First Name" onChange = {inputUser} />
    </div>
    <div className="field">
    <label>Last Name</label>
    <input type="text" name="last-name" placeholder="Last Name" onChange = {inputPass} />
    </div>
    <div className="field">
    </div>
  <button className="ui button blue" type="Submit">Add User</button>
  
  <h2>{fullName}</h2>
  
  </form>
    </div>
    </>
  )
}

export default AddContact
